mapper.update:
update db_bank_record
set bank_code  = #{bankCode}
  , flow_money = #{flowMoney}
  , type       = #{type}
  , source     = #{source}
where id = #{id}

mapper.select:
select id,
       bank_code  as bankCode,
       flow_money as flowMoney,
       type       as type,
       source     as source
from db_bank_record
where type = #{type}
  and source = #{source}
order by id DESC;
