mapper.select:
select dbbr.id, dbbr.bank_code as bankCode, dbbr.flow_money as flowMoney, dbbr.type as type, dbbr.source as source from db_bank_record dbbr left join db_bank_card dbbc on dbbc.card_code=dbbr.bank_code where 1=1
and dbbc.user_id=#{userId}
order by id DESC limit #{page} , #{limit};

mapper.update:
update db_bank_record
set bank_code  = #{bankCode}
  , flow_money = #{flowMoney}
  , type       = #{type}
  , source     = #{source}
where id = #{id}

mapper.select:
select id,
       bank_code  as bankCode,
       flow_money as flowMoney,
       type       as type,
       source     as source
from db_bank_record
where type = #{type}
  and source = #{source}
order by id DESC;
