mapper.select:
select dbfp.name as name, dbfp.annual_income as annualIncome, dbfp.pre_income as preIncome, dbfp.inves_term as invesTerm, dbfp.inves_money as invesMoney, dbau.admin_fullname as adminFullname, dbfpu.bank_card as bankCard, dbfpu.money as money, dbfpu.create_time as createTime from db_financial_products_user dbfpu join db_financial_products dbfp on dbfpu.financial_products_id = dbfp.id join db_admin_user dbau on dbfpu.user_id = dbau.id
where 1=1
and dbfpu.user_id=#{userId}
and (dbfp.name LIKE concat(concat('%',#{searchName}),'%'))
order by dbfpu.id DESC limit #{page} , #{limit};

select count(*) from db_financial_products_user dbfpu join db_financial_products dbfp on dbfpu.financial_products_id = dbfp.id where 1=1
and user_id=#{userId}
and (dbfp.name LIKE concat(concat('%',#{searchName}),'%'))

select id, name as name, annual_income as annualIncome, pre_income as preIncome, inves_term as invesTerm, inves_money as invesMoney from db_financial_products where 1=1
and (name LIKE concat(concat('%',#{searchName}),'%'))
order by id DESC limit #{page} , #{limit};

mapper.update:
update db_financial_products
set name          = #{name}
  , annual_income = #{annualIncome}
  , pre_income    = #{preIncome}
  , inves_term    = #{inves_term}
  , inves_money   = #{invesMoney}
where id = #{id}
